import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import axios from "axios";

const AdminOrders = () => {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user || !user.isAdmin) {
      navigate("/");
    } else {
      fetchOrders();
    }
  }, [user, navigate]);

  const fetchOrders = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/admin/orders");
      setOrders(res.data);
    } catch (error) {
      console.error("Error fetching orders:", error);
    } finally {
      setLoading(false);
    }
  };

  const updateOrderStatus = async (orderId, newStatus) => {
    try {
      await axios.put(`http://localhost:5000/api/admin/orders/${orderId}`, {
        orderStatus: newStatus,
      });
      alert("Cập nhật trạng thái thành công!");
      fetchOrders();
    } catch (error) {
      alert("Có lỗi xảy ra!");
    }
  };

  const getStatusText = (status) => {
    const statusMap = {
      pending: "Chờ xử lý",
      processing: "Đang xử lý",
      shipped: "Đang giao",
      delivered: "Đã giao",
      cancelled: "Đã hủy",
    };
    return statusMap[status] || status;
  };

  if (!user || !user.isAdmin) return null;
  if (loading) return <div className="loading">Đang tải...</div>;

  return (
    <div className="admin-orders">
      <h1>Quản lý đơn hàng</h1>

      <div className="orders-table">
        <table>
          <thead>
            <tr>
              <th>Mã đơn</th>
              <th>Khách hàng</th>
              <th>Ngày đặt</th>
              <th>Tổng tiền</th>
              <th>Thanh toán</th>
              <th>Trạng thái</th>
              <th>Thao tác</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order._id}>
                <td>{order._id.slice(-8)}</td>
                <td>
                  <div>
                    <strong>{order.user?.name}</strong>
                    <br />
                    <small>{order.user?.email}</small>
                  </div>
                </td>
                <td>{new Date(order.createdAt).toLocaleDateString("vi-VN")}</td>
                <td>{order.totalAmount.toLocaleString("vi-VN")} đ</td>
                <td>
                  <span className={`badge badge-${order.paymentStatus}`}>
                    {order.paymentStatus === "paid"
                      ? "Đã thanh toán"
                      : "Chờ thanh toán"}
                  </span>
                </td>
                <td>
                  <select
                    value={order.orderStatus}
                    onChange={(e) =>
                      updateOrderStatus(order._id, e.target.value)
                    }
                    className="status-select"
                  >
                    <option value="pending">Chờ xử lý</option>
                    <option value="processing">Đang xử lý</option>
                    <option value="shipped">Đang giao</option>
                    <option value="delivered">Đã giao</option>
                    <option value="cancelled">Đã hủy</option>
                  </select>
                </td>
                <td>
                  <button
                    onClick={() => {
                      alert(
                        `Chi tiết đơn hàng:\n\nMã: ${order._id}\nKhách hàng: ${order.user?.name}\nĐịa chỉ: ${order.shippingAddress.address}\nSĐT: ${order.shippingAddress.phone}`
                      );
                    }}
                    className="btn-view"
                  >
                    Xem
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminOrders;
