import React, {
  createContext,
  useState,
  useEffect,
  useContext,
  useRef,
} from "react";
import { AuthContext } from "./AuthContext"; // Import để lấy user/token
import axios from "axios";

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]); // [{ _id, name, price, imageUrl, quantity }]
  const authContext = useContext(AuthContext); // Lấy full AuthContext
  const { user } = authContext || {}; // Use user presence (AuthContext sets axios header)
  const prevUserIdRef = useRef(null); // Track user trước để detect switch

  // Load local cart khi init (chạy đầu tiên)
  useEffect(() => {
    const savedCart = localStorage.getItem("cart");
    if (savedCart) {
      try {
        const parsedCart = JSON.parse(savedCart);
        setCart(parsedCart);
      } catch (err) {
        console.error("Failed parse local cart:", err);
      }
    }
  }, []);

  // Sync localStorage khi cart thay đổi (chỉ lưu guest/local state)
  useEffect(() => {
    if (!user) {
      // Nếu guest thì lưu local
      try {
        localStorage.setItem("cart", JSON.stringify(cart));
      } catch (err) {
        console.error("Failed save cart to localStorage:", err);
      }
    } else {
      // Nếu logged in, không ghi đè local cart — đảm bảo localStorage rỗng để tránh leak
      try {
        localStorage.removeItem("cart");
      } catch (err) {
        // ignore
      }
    }
  }, [cart, user]);

  // Khi user thay đổi (login/logout/switch) => xử lý fetch/merge/clear
  useEffect(() => {
    // If user logged in
    if (user) {
      // Only run merge/fetch when user becomes different/new
      if (prevUserIdRef.current !== user._id) {
        (async () => {
          try {
            // If local guest cart exists -> merge into server
            const raw = localStorage.getItem("cart");
            if (raw) {
              let guestItems = [];
              try {
                const parsed = JSON.parse(raw);
                // Normalize guest items to [{ productId, quantity }]
                guestItems = parsed
                  .map((it) => {
                    const productId = it._id || it.productId || it.product;
                    const quantity = Number(it.quantity) || 1;
                    if (!productId || quantity <= 0) return null;
                    return { productId, quantity };
                  })
                  .filter(Boolean);
              } catch (err) {
                console.error("Invalid local cart format", err);
              }

              if (guestItems.length > 0) {
                try {
                  console.log("Merging guest cart into server...", guestItems);
                  await axios.post("http://localhost:5000/api/cart/merge", {
                    items: guestItems,
                  });
                } catch (err) {
                  console.error(
                    "Merge cart failed:",
                    err.response?.data || err.message
                  );
                }
              }
              // After attempting merge, remove local guest cart
              try {
                localStorage.removeItem("cart");
              } catch (err) {}
            }

            // Fetch latest server cart
            await fetchCartFromServer();
          } finally {
            prevUserIdRef.current = user._id;
          }
        })();
      }
    } else {
      // Logout or guest mode: clear in-memory cart to avoid showing previous user's items
      prevUserIdRef.current = null;
      setCart([]);
      // Keep localStorage 'cart' empty now - user will add guest items again if needed
      try {
        localStorage.removeItem("cart");
      } catch (err) {}
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [user]);

  const fetchCartFromServer = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/cart");
      // backend returns cart object with items (populated)
      const serverCart = (res.data && res.data.items) || [];
      const mapped = serverCart.map((it) => {
        const prod = it.product || {};
        const prodId =
          typeof prod === "object" && prod._id
            ? prod._id.toString()
            : it.product
            ? it.product.toString()
            : "";
        return {
          _id: prodId,
          name: it.name || prod.name || "Unknown",
          price: it.price != null ? it.price : prod.price || 0,
          imageUrl: it.imageUrl || prod.imageUrl || "",
          quantity: it.quantity || 1,
        };
      });
      setCart(mapped);
    } catch (error) {
      console.error(
        "Error fetching server cart:",
        error.response?.data || error.message
      );
      // keep local cart if server fails
    }
  };

  // Helper sync to server (kept for other updates) — returns promise
  const syncToServer = async (action, productId = null, quantity = null) => {
    if (!user) return;

    try {
      let response;
      switch (action) {
        case "update":
          response = await axios.post("http://localhost:5000/api/cart/add", {
            productId,
            quantity,
          });
          break;
        case "remove":
          response = await axios.delete(
            `http://localhost:5000/api/cart/remove/${productId}`
          );
          break;
        case "clear":
          response = await axios.delete("http://localhost:5000/api/cart/clear");
          break;
        default:
          return;
      }

      if (response && response.data && response.data.items) {
        const mapped = response.data.items.map((it) => {
          const prod = it.product || {};
          const prodId =
            typeof prod === "object" && prod._id
              ? prod._id.toString()
              : it.product
              ? it.product.toString()
              : "";
          return {
            _id: prodId,
            name: it.name || prod.name || "Unknown",
            price: it.price != null ? it.price : prod.price || 0,
            imageUrl: it.imageUrl || prod.imageUrl || "",
            quantity: it.quantity || 1,
          };
        });
        setCart(mapped);
      } else {
        await fetchCartFromServer();
      }
    } catch (error) {
      console.error(
        `Sync ${action} failed:`,
        error.response?.data || error.message
      );
      throw error;
    }
  };

  /**
   * addToCart: khi user đã login -> gọi API lưu vào DB (POST /api/cart/add)
   * nếu guest -> lưu localStorage như trước.
   * Trả về cart mới (Promise). Dùng async/await khi gọi.
   */
  const addToCart = async (product, qty = 1) => {
    if (!product || !product._id) {
      throw new Error("Invalid product");
    }

    // If user logged in -> call server and update state from response
    if (user) {
      try {
        const res = await axios.post("http://localhost:5000/api/cart/add", {
          productId: product._id,
          quantity: qty,
        });
        if (res && res.data && res.data.items) {
          const mapped = res.data.items.map((it) => {
            const prod = it.product || {};
            const prodId =
              typeof prod === "object" && prod._id
                ? prod._id.toString()
                : it.product
                ? it.product.toString()
                : "";
            return {
              _id: prodId,
              name: it.name || prod.name || "Unknown",
              price: it.price != null ? it.price : prod.price || 0,
              imageUrl: it.imageUrl || prod.imageUrl || "",
              quantity: it.quantity || 1,
            };
          });
          setCart(mapped);
          return mapped;
        } else {
          // fallback refetch
          await fetchCartFromServer();
          return cart;
        }
      } catch (error) {
        console.error(
          "Error addToCart (server):",
          error.response?.data || error.message
        );
        throw error;
      }
    }

    // Guest flow: update local cart and localStorage
    const existing = cart.find((item) => item._id === product._id);
    let newCart;
    if (existing) {
      newCart = cart.map((item) =>
        item._id === product._id
          ? { ...item, quantity: item.quantity + qty }
          : item
      );
    } else {
      newCart = [
        ...cart,
        {
          _id: product._id,
          name: product.name,
          price: product.price || 0,
          imageUrl: product.imageUrl || "",
          quantity: qty,
        },
      ];
    }
    setCart(newCart);
    try {
      localStorage.setItem("cart", JSON.stringify(newCart));
    } catch (err) {
      console.error("Failed save cart to localStorage:", err);
    }
    return newCart;
  };

  const removeFromCart = (productId) => {
    const newCart = cart.filter((item) => item._id !== productId);
    setCart(newCart);
    if (user) {
      syncToServer("remove", productId).catch(() => {});
    } else {
      try {
        localStorage.setItem("cart", JSON.stringify(newCart));
      } catch (err) {}
    }
  };

  const updateQuantity = (productId, quantity) => {
    if (quantity <= 0) {
      removeFromCart(productId);
      return;
    }
    const newCart = cart.map((item) =>
      item._id === productId ? { ...item, quantity } : item
    );
    setCart(newCart);
    if (user) {
      syncToServer("update", productId, quantity).catch(() => {});
    } else {
      try {
        localStorage.setItem("cart", JSON.stringify(newCart));
      } catch (err) {}
    }
  };

  const clearCart = () => {
    setCart([]);
    if (user) {
      syncToServer("clear").catch(() => {});
    } else {
      try {
        localStorage.removeItem("cart");
      } catch (err) {}
    }
  };

  const getTotal = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  return (
    <CartContext.Provider
      value={{
        cart,
        addToCart,
        removeFromCart,
        updateQuantity,
        clearCart,
        getTotal,
      }}
    >
      {children}
    </CartContext.Provider>
  );
};
